import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Spinner } from "../components/Spinner";
import styles from "./MovieDetails.module.css";
import { GrFavorite } from "react-icons/gr";
import { useSelector, useDispatch } from "react-redux";
import { addFavorite, getFavorites, removeFavorite } from "../store/favorites";
import Alert from 'react-bootstrap/Alert';

const MovieDetails = () => {
  const { movie_id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [movie, setMovie] = useState(null);
  const dispatch = useDispatch();

  const [showAlert, setShowAlert] = useState(false);

  // const showAlert = () => {
  //   alert ("The movie has been successfully added to your list")
  // }

  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    setIsLoading(true);
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${movie_id}?api_key=859094381e307b3a810be68bf946419d&language=en-US`
      )
      .then((data) => {
        setIsLoading(false);
        setMovie(data.data);
      })
      .catch((err) => console.log(err));
  }, [movie_id]);

  if (isLoading) {
    return <Spinner />;
  }


  if (!movie) return null;

  const handleAddFavorite = (movie) => {
    dispatch(addFavorite(movie)).then(() => dispatch(getFavorites()));
    setShowAlert(true)
  };

  return (
    <div className={styles.detailsContainer}>
      <img
        className={`${styles.col} ${styles.movieImage}`}
        src={`https://image.tmdb.org/t/p/w600_and_h900_bestv2${movie.poster_path}`}
        alt={movie.title}
      />
      <div className={`${styles.col} ${styles.movieDetails}`}>
        <h2 className={styles.firstItem}>
          <strong>Title: </strong>
          {movie.title}
        </h2>
        {
          <p>
            <strong>Genres: </strong>
            {movie.genres
              .map((genre) => genre)
              .map((gen) => gen.name)
              .join(", ") + "."}
          </p>
        }
        <p >
          <strong>Description: </strong>
          {movie.overview}
        </p>
        <p>
          <strong>Rating: </strong>
          {movie.vote_average.toFixed(1)}
        </p>

        <div className="controls">
          {user ? (
            <button type="button" className="btn btn-danger btn-sm">
              <Alert variant="success" show={showAlert} onClose={() => (setShowAlert(false))} dismissible>
        <Alert.Heading>¡Atención!</Alert.Heading>
        <p>
        "The movie has been successfully added to your list"
        </p>
      </Alert>
              <GrFavorite onClick={() => handleAddFavorite(movie) }  />
            </button>
          ) : null}
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
